export const addHobby = (artwalkId, hobby) => ({
  type: 'ADD_HOBBY',
  hobby: hobby,
  artwalkId: artwalkId
});

export const addKitten = (id, name, hobbies) => ({
  type: 'ADD_KITTEN',
  id: id,
  name: name,
  hobbies: hobbies
});

const API_URL = process.env.REACT_APP_API_URL;

export const fetchKittens = _ => async function (dispatch) {
  const url = `${API_URL}/artwalks`;
  console.log(`Fetching from ${url}`);
  const result = await fetch(url);
  const data = await result.json();
  for (const artwalk of data) {
    dispatch(addKitten(artwalk._id, artwalk.name, artwalk.hobbies));
  }
};

export const postArtwalk = (name) => async function (dispatch) {
  const response = await fetch(`${API_URL}/artwalks`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json; charset=utf-8"
    },
    body: JSON.stringify({ name: name })
  });
  const newArtwalk = await response.json();
  dispatch(addArtwalk(newArtwalk._id, newArtwalk.name, newArtwalk.hobbies));
};

export const postHobby = (artwalkId, hobby) => async function (dispatch) {
  const response = await fetch(`${API_URL}/artwalks/${artwalkId}/hobbies`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json; charset=utf-8"
    },
    body: JSON.stringify({ hobby: hobby })
  });
  const artwalk = await response.json();
  dispatch(addHobby(artwalk._id, hobby));
};
